<div class="kt-grid__item kt-grid__item--fluid kt-app__content"  id="edit_expense_div" ng-app="edit_expense_app" ng-controller="edit_expense_controller" ng-cloak ng-init="loadExpenseCategoryData()">    <div class="kt-subheader   kt-grid__item" id="kt_subheader">        <div class="kt-container  kt-container--fluid ">            <div class="kt-subheader__main">                <h3 class="kt-subheader__title">                    View Expense category                </h3>                <span class="kt-subheader__separator kt-subheader__separator--v"></span>            </div>        </div>    </div>    <div class="row hide" id="success_alert">        <div class="col-xl-12">            <div class="alert alert-outline-success fade show" role="alert">                <div class="alert-icon"><i class="flaticon2-check-mark"></i></div>                <div class="alert-text">Successfully updated.</div>            </div>        </div>    </div>    <div class="row hide" id="failed_alert">        <div class="col-xl-12">            <div class="alert alert-outline-danger fade show" role="alert">                <div class="alert-icon"><i class="flaticon-questions-circular-button"></i></div>                <div class="alert-text">Failed to update</div>            </div>        </div>    </div>    <div class="row hide" id="warning_alert">        <div class="col-xl-12">            <div class="alert alert-outline-warning fade show" role="alert">                <div class="alert-icon"><i class="flaticon-warning"></i></div>                <div class="alert-text">Expense category already exists</div>            </div>        </div>    </div>    <div class="row">        <div class="col-xl-12">            <!--begin:: Widgets/Trends-->            <div class="kt-portlet kt-portlet--head--noborder kt-portlet--height-fluid">                <form class="kt-form kt-form--label-right">                    <div class="kt-portlet__body">                        <div class="form-group row ">                            <!--<div class="col-md-4">                                <label>Expense type<span class="req">*</span></label>                                <div class="kt-radio-inline">                                    <label class="kt-radio">                                        <input type="radio" name="expense_type" value="1" ng-model="expenseType" disabled> Fixed                                        <span></span>                                    </label>                                    <label class="kt-radio">                                        <input type="radio" name="expense_type" value="2" ng-model="expenseType" disabled> Variable                                        <span></span>                                    </label>                                </div>                                <span class="form-text text-muted expense_type-error error-span"></span>                            </div>-->                            <div class="col-md-6">                                <label>Expense category<span class="req">*</span></label>                                <input type="text" class="form-control" aria-describedby="emailHelp" name="expense_category" id="expense_category" placeholder="Enter Expense Category" value="{{expenseCategory}}" required disabled>                                <span class="form-text text-muted expense_category-error error-span"></span>                            </div>                            <div class="col-md-6">                                <label>Status<span class="req">*</span></label>                                <select class="form-control" id="expense_status" name="expense_status" ng-model="expenseCategoryStatus" disabled>                                    <option value="1">Active</option>                                    <option value="0">Inactive</option>                                </select>                                <span class="form-text text-muted expense_status-error error-span"></span>                            </div>                            <div class="col-md-12 pt-12">                                <div class="text-center">                                    <button type="button" class="btn btn-secondary" ng-click="goBack()">Back</button>                                </div>                            </div>                        </div>                    </div>                </form>            </div>            <!--end:: Widgets/Trends-->        </div>    </div></div><script type="text/javascript">    var edit_expense_app = angular.module('edit_expense_app', []);    edit_expense_app.controller('edit_expense_controller', ['$scope', '$http', '$compile', '$location', '$window', function ($scope, $http, $compile, $location, $window) {            $scope.loadExpenseCategoryData = function () {                var url = $location.absUrl();                var expense_category_id = url.substring(url.lastIndexOf('/') + 1);                if (expense_category_id) {                    $http.get(base_url + 'masters/expenses/getExpenseCategoryData?id=' + expense_category_id)                            .success(function (response) {                                $scope.expenseType = response[0]['expense_type'];                                $scope.expenseCategory = response[0]['expense_category'];                                $scope.expenseCategoryStatus = response[0]['status'];                            })                }            }            $scope.goBack = function () {                $window.location.href = base_url + "masters/expenses";            }        }]);    var edit_expense_div = document.getElementById('edit_expense_div');    angular.element(document).ready(function () {        angular.bootstrap(edit_expense_div, ['edit_expense_app']);    });</script>