<style>    table tr td:nth-child(1){text-align:center;}    table tr td:nth-child(2){text-align:center;}    table tr td:nth-child(3){text-align:center;}    .table tr td {padding: 0rem; vertical-align:middle !important;}    .table tr th {padding: 0.7rem;}    table tr td p {vertical-align:middle !important; margin-top: 0.5rem; margin-bottom: 0.5rem;}    .table th, .table td {border-top: 0px solid #ebedf2;}    .table td {border-bottom: 1px solid #ebedf2;}    .dataTables_wrapper .dataTable{margin: 0rem 0 !important;}    .kt-portlet .kt-portlet__body{padding: 0px; }</style><div class="kt-content  kt-grid__item kt-grid__item--fluid kt-grid kt-grid--hor"  id="position_history_div" ng-app="position_history_app" ng-controller="position_history_controller" ng-init="pageLoad()" ng-cloak>    <div class="kt-subheader   kt-grid__item" id="kt_subheader">        <div class="kt-container  kt-container--fluid ">            <div class="kt-subheader__main">                <h3 class="kt-subheader__title">                    Position history of {{memberName}}                </h3>            </div>        </div>    </div>    <div class="kt-container  kt-container--fluid  kt-grid__item kt-grid__item--fluid" >        <div class="kt-portlet kt-portlet--mobile">            <div class="kt-portlet__body">                <!--begin: Datatable -->                <div id="kt_table_1_wrapper" class="dataTables_wrapper dt-bootstrap4 no-footer">                    <div class="row">                        <div class="col-sm-12"><table class="table table-hover table-checkable dataTable no-footer dtr-inline" id="kt_table_1" role="grid" aria-describedby="kt_table_1_info" style="width: 1461px;">                                <thead>                                    <tr role="row">                                        <th style="width: 61.25px;">Date From </th>                                        <th style="width: 61.25px;">Date To </th>                                        <th style="width: 161.25px;">Position</th>                                </thead>                                <tbody>                                    <tr  ng-repeat="(key, position) in positionHistory">                                        <td>                                            <p ng-if="position.date_from != '0000-00-00'">{{position.date_from| date : "dd/MM/yyyy"}}</p>                                            <p ng-if="position.date_from == '0000-00-00'">Till now</p>                                        </td>                                        <td>                                            <p ng-if="position.date_to != '0000-00-00'">{{position.date_to| date : "dd/MM/yyyy"}}</p>                                            <p ng-if="position.date_to == '0000-00-00'">Till now</p>                                        </td>                                        <td>                                            {{position.position_name}}                                        </td>                                    </tr>                                </tbody>                            </table>                        </div>                    </div>                    <!--end: Datatable -->                </div>                <div class="form-group row ">                    <div class="col-md-12 pt-12">                        <div class="text-center">                            <button type="button" class="btn btn-secondary" ng-click="goBack()">Back</button>                        </div><br/>                    </div>                </div>            </div>        </div>    </div></div><script>    var position_history_app = angular.module('position_history_app', []);    position_history_app.controller('position_history_controller', ['$scope', '$http', '$compile', '$location', '$window', '$timeout', function ($scope, $http, $compile, $location, $window, $timeout) {            $scope.pageLoad = function () {                var url = $location.absUrl();                var member_id = url.substring(url.lastIndexOf('/') + 1);                if (member_id) {                    $http.get(base_url + 'members/getPositionHistory?id=' + member_id)                            .success(function (response) {                                console.log(response);                                $scope.memberName = response['member_data'][0]['name'];                                $scope.positionHistory = response['position_history'];                            })                }            }            $scope.goBack = function () {                $window.location.href = base_url + "members";            }        }]);    var position_history_div = document.getElementById('position_history_div');    //alert(dvSecond);    angular.element(document).ready(function () {        angular.bootstrap(position_history_div, ['position_history_app']);    });</script>