kt-input-icon kt-input-icon--right kt-subheader__search">                            <input type="text" class="form-control" placeholder="From Date" id="from_date">                            <span class="kt-input-icon__icon kt-input-icon__icon--right">                                <span>                                    <i class="flaticon2-calendar"></i>                                </span>                            </span>                        </div>                    </form>                </div>                <div class="kt-subheader__group" id="kt_subheader_search">                    <form class="kt-margin-l-20" id="kt_subheader_search_form">                        <div class="kt-input-icon kt-input-icon--right kt-subheader__search">                            <input type="text" class="form-control" placeholder="To Date" id="to_date">                            <span class="kt-input-icon__icon kt-input-icon__icon--right">                                <span>                                    <i class="flaticon2-calendar"></i>                                </span>                            </span>                        </div>                    </form>                </div>                <div class="kt-subheader__toolbar" >                    <a class="btn btn-label-brand btn-bold m-l-15" id="date_search" ng-click='dateSearch()'>                        <i class="flaticon2-magnifier-tool"></i></a>                    <a class="btn btn-label-danger btn-bold" id="reload" ng-click='reloadDT()'>                        <i class="flaticon2-refresh-button"></i></a>                </div>            </div>            <div class="kt-subheader__main">                <div class="dropdown dropdown-inline">                    <button type="button" class="btn btn-primary btn-icon-sm dropdown-toggle" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">                        <i class="la la-download"></i> Export                    </button>                    <div class="dropdown-menu dropdown-menu-right">                        <ul class="kt-nav">                            <li class="kt-nav__item" id="current_data_export">                                <a href="#" class="kt-nav__link">                                    <i class="kt-nav__link-icon la la-file-excel-o"></i>                                    <span class="kt-nav__link-text">Current data</span>                                </a>                            </li>                            <li class="kt-nav__item" id="entire_data_export">                                <a href="#" class="kt-nav__link">                                    <i class="kt-nav__link-icon la la-file-excel-o"></i>                                    <span class="kt-nav__link-text">Entire data</span>                                </a>                            </li>                        </ul>                    </div>                </div>                <div class="kt-subheader__toolbar" id="add_btn_div" ng-if="isVisible">                    <a class="btn btn-label-brand btn-bold" ng-click='addPettyCash()'>                        <i class="flaticon2-plus"></i> Add New </a>                </div>            </div>        </div>        <input type="hidden" id="hasEdit" value="0">        <input type="hidden" id="hasDelete" value="0">    </div>    <!-- begin:: Content -->    <div class="kt-container  kt-container--fluid  kt-grid__item kt-grid__item--fluid" >        <div class="kt-portlet kt-portlet--mobile">            <!--<div class="kt-portlet__body">                <div class="kt-form kt-form--label-right">                    <div class="row align-items-center">                        <div class="col-xl-8 order-2 order-xl-1">                            <div class="col-md-4 kt-margin-b-20-tablet-and-mobile">                                <div class="kt-input-icon kt-input-icon--left">                                    <input type="text" class="form-control" placeholder="Search..." id="generalSearch">                                    <span class="kt-input-icon__icon kt-input-icon__icon--left">                                        <span><i class="la la-search"></i></span>                                    </span>                                </div>                            </div>                        </div>                        <div class="col-xl-4 order-1 order-xl-2 kt-align-right">                            <a href="#" class="btn btn-default kt-hidden">                                <i class="la la-cart-plus"></i> New Order                            </a>                            <div class="kt-separator kt-separator--border-dashed kt-separator--space-lg d-xl-none"></div>                        </div>                    </div>                </div>            </div>-->            <div class="kt-portlet__body kt-portlet__body--fit">                <!--begin: Datatable -->                <div class="petty-cash-datatable" id="ajax_data"></div>                <!--end: Datatable -->            </div>        </div>	</div>    <!-- end:: Content --></div><div class="modal fade" id="profile_img_popup" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">    <div class="modal-dialog modal-dialog-centered" role="document">        <div class="modal-content">            <div class="modal-header">                <h5 class="modal-title" id="exampleModalLongTitle">Profile Image</h5>                <button type="button" class="close" data-dismiss="modal" aria-label="Close">                </button>            </div>            <div class="modal-body ">                <img class="popup_profile_img" src="<?php echo base_url(); ?>attachments/profile_image/default.png" style="width:465px;height:auto;">            </div>            <!--<div class="modal-footer">                <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>            </div>-->        </div>    </div></div><div id="export_excel"></div><script>    var petty_cash_app = angular.module('petty_cash_app', []);    petty_cash_app.controller('petty_cash_controller', ['$scope', '$http', '$compile', '$location', '$window', function ($scope, $http, $compile, $location, $window) {            $scope.loadPettyCashPage = function () {                $http.get(base_url + 'users/getPermissions')                        .success(function (response) {                            console.log(response);                            var modules_permission = response.modules;                            $scope.isVisible = "false";                            if (modules_permission['petty_cash']) {                                if (modules_permission['petty_cash']['petty_cash']) {                                    if (modules_permission['petty_cash']['petty_cash']['add'] == "1") {                                        // alert("masters");                                        $scope.isVisible = "true";                                        // The == and != operators consider null equal to only null or undefined                                    }                                    if (modules_permission['petty_cash']['petty_cash']['edit'] == "1") {                                        // alert("masters");                                        $("#hasEdit").val("1");                                        // The == and != operators consider null equal to only null or undefined                                    }                                    if (modules_permission['petty_cash']['petty_cash']['delete'] == "1") {                                        // alert("masters");                                        $("#hasDelete").val("1");                                        // The == and != operators consider null equal to only null or undefined                                    }                                    pettyCashDataTable.init();                                    KTFormWidgets.init();                                    setTimeout(function () {                                        //pettyCashDataTable.footer();                                    }, 5000);                                } else {                                    $window.location.href = base_url + "dashboard";                                }                            } else {                                $window.location.href = base_url + "dashboard";                            }                        })            };            $scope.addPettyCash = function () {                $window.location.href = base_url + "petty_cash/add";            }//            $scope.dateSearch = function () {////                membersDataTable.reload();//            }            //demo();        }]);    var petty_cash_div = document.getElementById('petty_cash_div');    //alert(dvSecond);    angular.element(document).ready(function () {        angular.bootstrap(petty_cash_div, ['petty_cash_app']);    });    var pettyCashDataTable = function () {        // Private functions        // basic demo        var demo = function () {            var form_date = $("#from_date").val();            var to_date = $("#to_date").val();            var fromDate = "";            if (form_date) {                fromDate = convertDate(form_date);            }            var toDate = "";            if (to_date) {                toDate = convertDate(to_date);            }            //alert("datatable");            var current_date = "";            var balance = 0;            var datatable = $('.petty-cash-datatable').KTDatatable({                // datasource definition                data: {                    type: 'remote',                    method: 'POST',                    source: {                        read: {                            //url: 'https://keenthemes.com/metronic/tools/preview/inc/api/datatables/demos/default.php',                            url: base_url + 'petty_cash/getPettyCash',                            map: function (raw) {                                console.log(raw);                                var indexNumber = 0;                                if (typeof raw.data !== 'undefined') {                                    var dataSet = raw.data;                                    $.each(dataSet, function (key, value) {                                        dataSet[key].indexNumber = parseInt(key) + 1;                                    });                                }                                return dataSet;                            },                        },                    },                    pageSize: 10,                    serverPaging: false,                    serverFiltering: true,                    serverSorting: false,                    saveState: {                        cookie: true,                        webstorage: true,                    },                },                // layout definition                layout: {                    scroll: false, // enable/disable datatable scroll both horizontal and vertical when needed.                    footer: false, // display/hide footer                    spinner: false,                },                // column sorting                sortable: true,                pagination: true,                search: {                    input: $('#generalSearch'),                },                // columns definition                columns: [                    {                        field: 'indexNumber',                        title: '#',                        sortable: 'asc',                        type: 'number',                        selector: false,                        textAlign: 'center',                        template: function (row) {                            return '<span class="">' + row.indexNumber + '</span>';                        },                    },                    {                        field: 'expense_category_name',                        title: 'Category',                        template: function (row) {                            //console.log(row);                            return '<span class="bold-name txt-tf-caps ">' + row.expense_category_name + '</span>';                        },                    },                    {                        field: 'expense_type',                        title: 'Expense Type',                        width: 100,                        template: function (row) {                            //console.log(row);                            var status = {                                1: {'title': 'Fixed', 'class': ' btn-label-success'},                                2: {'title': 'Variable', 'class': ' btn-label-primary'},                            };                            return '<span class="btn btn-bold btn-sm btn-font-sm ' + status[row.expense_type].class + '">' + status[row.expense_type].title + '</span>';                            var status = {                                1: {'title': 'Fixed', 'class': ' kt-font-success'},                                2: {'title': 'Variable', 'class': ' kt-font-primary'},                            };                            var dotColor = {                                1: {'Fixed': 'Fixed', 'class': ' kt-badge--success'},                                2: {'Variable': 'Variable', 'class': ' kt-badge--primary'},                            };                            //alert("template");                            //console.log(row);                            return '<span style = "width: 110px;" > <span class = "kt-badge ' + dotColor[row.expense_type].class + ' kt-badge--dot" > </span>&nbsp;<span class="kt-font-bold ' + status[row.expense_type].class + '">' + status[row.expense_type].title + '</span> </span>';                            return '<span class="kt-badge ' + status[row.expense_type].class + ' kt-badge--inline kt-badge--pill">' + status[row.expense_type].title + '</span>';                        },                    }, {                        field: 'cash_in',                        title: 'Cash In',                        width: 100,                    },                    {                        field: 'cash_out',                        title: 'Cash Out',                        width: 100,                    },                    {                        field: 'balance',                        title: 'Balance',                        width: 100,                        template: function (row) {                            //console.log(row);                            //alert("dnfkjsdfkjds");                            if (current_date == "") {                                current_date = row.petty_cash_date;                            }                            if (current_date != row.petty_cash_date) {                                //current_date = row.petty_cash_date;                                current_date = row.petty_cash_date;                                balance = 0;                            }                            //alert(balance);                            if (row.transaction_type == "1" && row.expense_id != "2") {                                balance = balance + parseFloat(row.cash_in);                            } else {                                balance = balance - parseFloat(row.cash_out);                            }                            if (row.expense_id == "2") {                                return "-";                            } else {                                return  balance.toFixed(2);                            }                        },                    },                    {                        field: 'petty_cash_date',                        title: 'Date',                        template: function (row) {                            //console.log(row);                            var petty_cash_dateGet = row.petty_cash_date;                            var petty_cash_date = "-";                            if (petty_cash_dateGet != '0000-00-00' && petty_cash_dateGet != "") {                                var petty_cash_dateSplit = petty_cash_dateGet.split("-");                                var petty_cash_date = petty_cash_dateSplit[2] + "/" + petty_cash_dateSplit[1] + "/" + petty_cash_dateSplit[0];                            }                            return  petty_cash_date;                        },                    }, {                        field: 'Actions',                        title: 'Actions',                        sortable: false,                        overflow: 'visible',                        autoHide: false,                        template: function (row) {                            var hasEditPermission = $('#hasEdit').val();                            var hasDeletePermission = $('#hasDelete').val();                            var action = '\                                                \                                                <a href="javascript:;" class="btn btn-sm btn-clean btn-icon btn-icon-md petty_cash_view" data-id="' + row.petty_cash_id + '" title="View details">\                                                        <i class="kt-nav__link-icon flaticon-eye"></i>\                                                </a>\                                               \                                        ';                            if (hasEditPermission == "1") {                                action += '\                                    \                                                <a href="javascript:;" class="btn btn-sm btn-clean btn-icon btn-icon-md petty_cash_edit" data-id="' + row.petty_cash_id + '" ng-click="expenseTypeEdit()" title="Edit details">\                                                        <i class="kt-nav__link-icon flaticon2-contract"></i>\                                                </a>\                                                \                            ';                            }//                            if (hasDeletePermission == "1") {//                                action += '\//                                               \//                                                 <a href="javascript:;" class="btn btn-sm btn-clean btn-icon btn-icon-md receipt_delete" data-id="' + row.receipt_id + '" ng-click="expenseTypeDelete()" title="Delete">\//                                                        <i class="kt-nav__link-icon flaticon2-trash"></i>\//                                                </a>\//                                                \//                                        ';//                            }                            setTimeout(function () {                                showTotal();                            }, 500);                            return action;                        },                    }],                // fnRowCallback: function (nRow, aData, iDisplayIndex) {                //alert("rows");                //alert(iDisplayIndex);                //$("td:nth-child(1)", nRow).html(iDisplayIndex + 1);                //return nRow;                //}            });            setTimeout(function () {                showTotal();            }, 500);            function showTotal() {                $(".kt-datatable__table tbody").find(".total_row").remove();                $(".kt-datatable__table tbody").append('<tr data-row="" class="kt-datatable__row kt-datatable__row--even total_row" style="left: 0px;"><td class="kt-datatable__cell--sorted kt-datatable__cell--center kt-datatable__cell" data-field="indexNumber"><span style="width: 136px;"><span class=""></span></span></td><td data-field="expense_category_name" class="kt-datatable__cell"><span style="width: 136px;"><span class="bold-name txt-tf-caps"></span></span></td><td data-field="expense_type" class="kt-datatable__cell"><span style="width: 100px;"></span></td><td data-field="cash_in" class="kt-datatable__cell"><span style="width: 100px;">0</span></td><td data-field="cash_out" class="kt-datatable__cell"><span style="width: 100px;"></span></td><td data-field="balance" class="kt-datatable__cell"><span style="width: 100px;"></span></td><td data-field="petty_cash_date" class="kt-datatable__cell"><span style="width: 136px;"></span></td><td data-field="Actions" data-autohide-disabled="false" class="kt-datatable__cell"><span style="overflow: visible; position: relative; width: 136px;">                                                                                                                      </span></td>');                var cashInTotal = 0;                var cashOutTotal = 0;                $(".kt-datatable__table tbody tr").each(function () {                    var td = $(this).find("td:nth-child(4)").html();                    if (parseFloat($(td).text())) {                        cashInTotal = parseFloat(cashInTotal) + parseFloat($(td).text());                    }                    $('.kt-datatable__table tbody tr:last').find("td:nth-child(4)").html('<span style="width: 100px;background-color: #22b9ff;font-weight: 600;color: #fff;padding: 6px;">&#8377; ' + cashInTotal + '</span>');                });                $(".kt-datatable__table tbody tr").each(function () {                    var td = $(this).find("td:nth-child(5)").html();                    if (parseFloat($(td).text())) {                        cashOutTotal = parseFloat(cashOutTotal) + parseFloat($(td).text());                    }                    $('.kt-datatable__table tbody tr:last').find("td:nth-child(5)").html('<span style="width: 100px;background-color: #22b9ff;font-weight: 600;color: #fff;padding: 6px;">&#8377; ' + cashOutTotal + '</span>');                });            }            $(document).on('click', '.kt-datatable__pager-link-number', function () {                //alert("sajhdgj");                setTimeout(function () {                    showTotal();                }, 1000);            });            $('#kt_form_status').on('change', function () {                datatable.search($(this).val().toLowerCase(), 'Status');                setTimeout(function () {                    showTotal();                }, 1000);            });            $('#kt_form_type').on('change', function () {                datatable.search($(this).val().toLowerCase(), 'Type');                setTimeout(function () {                    showTotal();                }, 1000);            });            $('#generalSearch').on('keyup', function () {                var val = $(this).val().toLowerCase();                var value = {                    generalSearch: val,                }                datatable.setDataSourceParam('query', value);                datatable.load();                setTimeout(function () {                    showTotal();                }, 1000);            });            $('#date_search').on('click', function () {                var form_date = $("#from_date").val();                var to_date = $("#to_date").val();                var fromDate = "";                if (form_date) {                    fromDate = convertDate(form_date);                }                var toDate = "";                if (to_date) {                    toDate = convertDate(to_date);                }                var value = {                    fromDate: fromDate,                    toDate: toDate,                }                datatable.setDataSourceParam('query', value);                datatable.load();                setTimeout(function () {                    showTotal();                }, 1000);            });            $('#reload').on('click', function () {                $("#from_date").val("");                $("#to_date").val("");                var value = {                    generalSearch: "",                }                datatable.setDataSourceParam('query', value);                datatable.load();                setTimeout(function () {                    showTotal();                }, 1000);            });            //$('#kt_form_status,#kt_form_type').selectpicker();        };        var initFooter = function () {            var table = $('.kt-datatable__table');            // begin first table            table.DataTable({                //responsive: true,                //pageLength: 5,                //lengthMenu: [[2, 5, 10, 15, -1], [2, 5, 10, 15, 'All']],                footerCallback: function (row, data, start, end, display) {                    //alert("djfhkj");                    //return;                    var column = 4;                    var column3 = 3;                    var api = this.api(), data;                    console.log(api);                    // Remove the formatting to get integer data for summation                    var intVal = function (i) {                        return typeof i === 'string' ? i.replace(/[\$,]/g, '') * 1 : typeof i === 'number' ? i : 0;                    };                    // Total over this page                    var pageTotal = api.column(column, {page: 'current'}).data().reduce(function (a, b) {                        return intVal(a) + intVal($(b).text());                    }, 0);                    var pageTotal3 = api.column(column3, {page: 'current'}).data().reduce(function (a, b) {                        return intVal(a) + intVal($(b).text());                    }, 0);                    console.log($(api.column(column).footer()));                    // Update footer                    $(api.column(column).footer()).html(                            '$' + KTUtil.numberString(pageTotal.toFixed(2))                            );                    $(api.column(column3).footer()).html(                            '$' + KTUtil.numberString(pageTotal3.toFixed(2))                            );                },            });        };        return {            // public functions            init: function () {                demo();            },            reload: function () {                demo();            },            footer: function () {                initFooter();            }        };    }();    function convertDate(dateVal) {        var split_date = dateVal.split("/");        var converted_date = split_date[2] + "-" + split_date[0] + "-" + split_date[1];        return converted_date;    }    var KTFormWidgets = function () {        // Private functions        var initWidgets = function () {            $('#from_date').datepicker({                todayHighlight: true,                templates: {                    leftArrow: '<i class="la la-angle-left"></i>',                    rightArrow: '<i class="la la-angle-right"></i>'                }            });            $('#to_date').datepicker({                todayHighlight: true,                templates: {                    leftArrow: '<i class="la la-angle-left"></i>',                    rightArrow: '<i class="la la-angle-right"></i>'                }            });        }        return {            // public functions            init: function () {                initWidgets();            }        };    }();    $(document).on('click', '.petty_cash_edit', function () {        var petty_cash_id = $(this).attr("data-id");        window.location.href = base_url + "petty_cash/edit/" + petty_cash_id;    });    $(document).on('click', '.petty_cash_view', function () {        var petty_cash_id = $(this).attr("data-id");        window.location.href = base_url + "petty_cash/view/" + petty_cash_id;    });</script><script>    var jq = $.noConflict();    jq(document).on('click', '#current_data_export', function () {        fnExcelReport2();    });    function fnExcelReport2() {        //alert("export");        var tab_text = "<table id='custom_export' border='5px'><tr width='100px' bgcolor='#87AFC6'>";        var textRange;        var j = 0;        tab = document.getElementsByClassName("kt-datatable__table")[0];// id of table        //tab = $('table .kt-datatable__table'); // id of table        console.log(tab);        for (j = 0; j < tab.rows.length; j++)        {            tab_text = tab_text + tab.rows[j].innerHTML + "</tr>";            //tab_text=tab_text+"</tr>";        }        tab_text = tab_text + "</table>";        tab_text = tab_text.replace(/<A[^>]*>|<\/A>/g, ""); //remove if u want links in your table        tab_text = tab_text.replace(/<img[^>]*>/gi, ""); // remove if u want images in your table        tab_text = tab_text.replace(/<input[^>]*>|<\/input>/gi, ""); // reomves input params        jq('#export_excel').show();        jq('#export_excel').html('').html(tab_text);        jq('#export_excel').hide();        //alert("excel fn");        jq("#custom_export").table2excel({            exclude: ".noExl",            name: "Receipts Report",            filename: "Receipts-details",            fileext: ".xls",            exclude_img: false,            exclude_links: false,            exclude_inputs: false        });    }    jq(document).on('click', '#entire_data_export', function () {        var form_date = $("#from_date").val();        var to_date = $("#to_date").val();        var fromDate = "";        if (form_date) {            fromDate = convertDate(form_date);        }        var toDate = "";        if (to_date) {            toDate = convertDate(to_date);        }        var arr = [];        arr.push({'searchString': jq('#generalSearch').val()});        arr.push({'from': fromDate});        arr.push({'to': toDate});        var arrStr = JSON.stringify(arr);        window.location.replace(base_url + 'petty_cash/petty_cash_report?search=' + arrStr);    });</script>